data.divi_custom_css:
/* FORM STUFF */

textarea {width:100%!important;}
input {width:100%;}
label {display:inline; color:#F5BD00;}
.member-1-state select {width:100%;}

h1, h2, h3 {text-transform:uppercase;}
h3 {font-family:'Poppins', sans-serif;}
p {font-size:20px; line-height:40px;}


.form-name {width:50%; float:left;}
.form-phone {width:50%; float:left;}
.form-email {width:50%; float:left;}
.form-message {}

.white-bg-content h2 {color:#27536B;}
.blue-bg-content h2 {color:#F5BD00;}

.footer-text p {float:right; display:inline; margin-left:20px; font-size:14px; line-height:1.5;}

.footer-text p a {color:#27536B;}

.footer-logo, .footer-sm {display:inline-block; float:left;}

.header-menu {float:right!important; text-align:right!important;}

.planets-mobile {margin:auto;box-sizing:border-box;}

.spacefolk-mobile {width:120px; border:1px dashed green; box-sizing:border-box;}

/* DESKTOP & TABLET DISPLAY ONLY */
@media only screen and (min-width:768px) {
.planets {float: right; margin: 40px 0px 40px 40px;}
.spacefolk-mobile {width:initial;}
	
	/* FORM STUFF */
	input {width:33%; float:left; display:inline;}
	.member-1-state select {width:33%; float:left; display:inline;}
}
/* END */

.hidden{border:2px solid orange; visibility:hidden;}

.cm-txt-1 {display:initial;}
.cm-txt-2, .cm-txt-3 {display:none;}

/* 
 .cm-txt-1-hidden, .cm-txt-2-hidden, .cm-txt-3-hidden {display:none;} 
 */

.mobile-cm-column {max-width:33%!important;}

.cm-img-1 span img, .cm-img-2 span img, .cm-img-3 span img {margin:0px 10px; cursor:pointer;}
/* 
.et_pb_menu--style-centered .et_pb_menu__wrap {
    -ms-flex-pack: center;
	justify-content: right;}
*/

.et_pb_menu li ul {
padding: 0!important;
border-top: none;
}
.et_pb_menu li li {border:1px solid #27536B;}

.hero-heading {}

.hero-heading h1 {font-family: 'Alfa Slab One', cursive; color:#F5BD00; font-size:3.5rem; text-transform: uppercase;}

.hero-heading h1 span {font-family: 'Poppins', sans-serif; color:#ffffff; font-weight:400;}

.sub-menu {margin-top:80px;}
