data.divi_custom_css:
h1, h2, h3 {text-transform:uppercase;}
h3 {font-family:'Poppins', sans-serif;}

p {font-size:20px; line-height:40px;}

.white-bg-content h2 {color:#27536B;}

.blue-bg-content h2 {color:#F5BD00;}

.footer-text p {float:right; display:inline; margin-left:20px;}

.footer-text p a {color:#27536B;}

.footer-logo, .footer-sm {display:inline-block; float:left;}

.header-menu {float:right!important; text-align:right!important;}

/* */
.et_pb_menu--style-centered .et_pb_menu__wrap {
    -ms-flex-pack: center;
	justify-content: right;}

.sub-menu .menu-item {border-top:2px!important;}

.nav {}

.hero-heading {}

.hero-heading h1 {font-family: 'Alfa Slab One', cursive; color:#F5BD00; font-size:3.5rem; text-transform: uppercase;}

.hero-heading h1 span {font-family: 'Poppins', sans-serif; color:#ffffff; font-weight:400;}

.sub-menu {margin-top:80px;}
